
public class Euler_question_6 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 int sumOfthesquares=(100*(100+1)*((2*100)+1))/6;
int sumOfThenumber=(100*(100+1))/2;
int difference=((sumOfThenumber*sumOfThenumber)-sumOfthesquares);
System.out.println("Difference between the sum of the squares of the first one hundred natural numbers and the square of the sum is 
"+difference);

	}
}

